import {useState, useEffect} from 'react';
import Checkbox from './Checkbox';
import { db } from "./firebase";
import { collection, addDoc } from "firebase/firestore";

import { query, onSnapshot, doc, updateDoc, deleteDoc, orderBy, serverTimestamp, getDoc} from "firebase/firestore";

export function App() {
  const [list, setList] = useState([]);
  const [input, setInput] = useState("");
  const [cbState, setCbState] = useState([]);
  const [editOn, setEditOn] = useState(true);


  useEffect(() => {
    const collectionRef = collection(db, 'todo');
    const q = query(collectionRef, orderBy('createdAt'));
    const unsub = onSnapshot(q, (querySnapshot) => {
      let todoArray = [];
      let cbArray = [];
      querySnapshot.forEach((doc) => {
        todoArray.push({...doc.data(), id: doc.id});
        cbArray.push(doc.data().completed);
      });
      setList(todoArray);
      setCbState(cbArray);
    });
    return () => unsub();
  }, []);

  const addToDo = async (e) => {
    e.preventDefault();
    if (input !== "") {
      const newToDo = {
        toDo: input,
        completed: false,
        createdAt: serverTimestamp(),
      };

      await addDoc(collection(db, 'todo'), newToDo);

      // // add the todo to existing list (for non database method)
      // setList([...list, newToDo]);

      // clear input box
      setInput("");
    }
  };

  const handleEdit = async (todo) => { 
    const changeToMsg = // text in the input field
    await updateDoc(doc(db, 'todo', todo.id), {toDo: changeToMsg});
  };
  const toggleComplete = async (todo) => {
    await updateDoc(doc(db, 'todo', todo.id), { 
      completed: !todo.completed
    });
  };
  const handleDelete = async (id) => {
    await deleteDoc(doc(db, 'todo', id));
  };


  const viewMode = (<p>hehe</p>);
  const editMode = (
    <div className="App">
      <form onSubmit={addToDo}>
        <h1>To Do List</h1>
        <input 
        type="text"
        onChange={(e) => setInput(e.target.value)}
        value={input}
        ></input>
        <button type="submit">Add To Do</button>
      </form>
      <ul>
        {list.map((toDo,index) => (
          <div key={toDo.id} className={toDo.completed ? "strike" : ""}>
            <input 
            value={toDo.toDo} 
            type="checkbox" 
            checked={cbState[index]}
            onChange={() => toggleComplete(toDo)}/>
            <span>{toDo.toDo}</span>
            <button onClick={() => handleEdit(toDo)}>Edit</button>
            <button onClick={() => handleDelete(toDo.id)}>&times;</button>  
            <hr></hr>     
          </div>
        ))}
      </ul>
    </div>
  );

  return <li>{ editOn? editMode : viewMode}</li>;
}

export default App;
