import React, {useState} from 'react';

export function Checkbox() {
    const [todoList, setTodoList] = useState([{toDo: 'inputtttt',completed: false},{toDo: 'inputyyyyyyy',completed: false}]); // remove hehe and add a "addtodo" function that assigns index to its cb too
    const [cbState, setCbState] = useState([false, false]); // add spread operator

    const handleOnChange = (position) => {
        const updatedCheckedState = cbState.map((cb, index) =>
          index === position ? !cb : cb
        );
    }
    return (
        <div>
            <ul>
                {todoList.map((element, index) => (                 
                    <div>
                        <input
                        key={`cb${index}`}
                        id={index}
                        type="checkbox"
                        checked={cbState[index]}
                        onChange={() => handleOnChange(index)}
                        />
                        <span>{element}</span>
                    </div>
                ))
                }
            </ul>
        </div>
    )
};

export default Checkbox;
