import {useState, useEffect} from 'react';

// for firebase functionality/ saving functionality
import { db } from "./firebase";
import { collection, addDoc } from "firebase/firestore";

import { query, onSnapshot, doc, updateDoc, deleteDoc } from "firebase/firestore";
// is there e prevent default in here? ########## TODO
// Add use effect here ########## TODO


export function App() {
  const [list, setList] =useState([]);
  const [input, setInput] = useState(""); //title

  // firebase functionality
  useEffect(() => {
    const q = query(collection(db, 'todo'));
    const unsub = onSnapshot(q, (querySnapshot) => {
      let todoArray = [];
      querySnapshot.forEach((doc) => {
        todoArray.push({...doc.data(), id: doc.id});
      });
      setList(todoArray);
    });
    return () => unsub();
  }, []);

  // firebase functionality
  const handleEdit = async (todo, list) => {
    await updateDoc(doc(db, 'todo', todo.id), {list: list}); // check whether really is title or input
  };
  const toggleComplete = async (todo) => {
    await updateDoc(doc(db, 'todo', todo.id), { 
      completed: !todo.completed
    });
  };
  const handleDelete = async (id) => {
    await deleteDoc(doc(db, 'todo', id));
  };

  // edited for firebase functionality
  // Add todos to either local mem or firestore
  // https://www.koderhq.com/tutorial/vue/firestore-database/

  const addToDo = async (toDo) => {
    // title is input // input is input
    // input is e.target.value
    if (input !== "") {
      const newToDo = {
        id: Math.random(),
        toDo: toDo,
        completed: false
      };

      // add todo to database (for database method)
      const collectionRef = collection(db, 'todo');
      await addDoc(collectionRef, newToDo);

      // // add the todo to existing list (for non database method)
      // setList([...list, newToDo]);

      // clear input box
      setInput("");
    }
  };

  const deleteToDo = (id) => {
    const newList = list.filter(toDo => toDo.id !== id);
    setList(newList);
  }

  // // to apply strikethrough formatting to toDo if completed = true
  // const handleToggle = (id) => {
  //   let mapped = list.map(toDo => toDo.id == id ? { ...toDo, completed: !toDo.completed } : { ...toDo});
  //   setList(mapped);
  //   console.log(mapped);
  // };

  return (
    <div className="App">
      <h1>To Do List</h1>
      <input 
      type="text"
      onChange={(e) => setInput(e.target.value)}
      value={input}
      ></input>
      <button onClick={() => addToDo(input)}>Add To Do</button>
      <ul>
        {list.map((toDo) => (
          <div key={toDo.id} className={toDo.completed ? "strike" : ""}>
            <input value={toDo.toDo} type="checkbox" onClick={() => toggleComplete(toDo.id)}/>
            <span>{toDo.toDo}</span>
            <button onClick={() => handleDelete(toDo.id)}>&times;</button>  
            <hr></hr>     
          </div>
        ))}
      </ul>
    </div>
  );
}

export default App;
